INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'033c82e0-23b3-5933-7609-000000005292', N'“De trein die stilstond” - Een KAHAANIVertelling door Mieke Felix.', N'Op 19 april 1943 hielden drie jonge kerels in Boortmeerbeek een trein tegen die vertrokken was uit de Dossinkazerne. Ze waren gewapend met en pistool, een stormlamp en enkele tangen. Enkele zoutkorreltjes, meer was het niet. Eén van de jongens slaagde erin de wagondeur te openen.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-21T17:00:00.000' AS DateTime), CAST(N'2022-07-22T05:59:59.000' AS DateTime), N'96237748-ac40-469d-a52c-668e7fbde707')
