INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'034a154e-f1cd-55a3-b695-000000002151', N'K.M. De Melomanen - De Familie Potaarde in Spanje - Een wervelende komedie van Romain Deconinck', N'Net als elk jaar brengt KM De Melomanen een wervelende komedie van Romain Deconinck. Het jaar 1964. Alfred, de schoonzoon van Stien Potaarde is Europees judo kampioen geworden bij de politie. Als beloning mag hij samen met zijn hele familie en de familie Pycke op reis naar Spanje. Klein probleempje echter, in de reis is geen verblijf inbegrepen. Onder een verzengende hitte moet het gezelschap op zoek naar een passend, en liefst niet te duur logement. Gelukkig komt de lokale politie ter hulp en vinden ze een klein en vooral goedkoop hotel.  Ongewild helpen ze de politie in de zoektocht naar een onverlaat die nietsvermoedende toeristen besteelt.  Romain Deconinck schreef 13 stukken met de familie Potaarde in de hoofdrol. Dit stuk is het laatste en bovendien een van de beste uit deze reeks.', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-18T19:30:00.000' AS DateTime), CAST(N'2022-07-18T22:00:00.000' AS DateTime), N'ea48073c-ff13-4689-bb5a-137041d39f9a')
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'035668c8-f610-467e-b530-e76607aed9da', N'De Liedjesatlas - Peer en Stefan', N'Peer en Stefan reizen in deze voorstelling samen met hun jonge publiek de wereld rond en zingen de leukste traditionals van de landen die ze aandoen. Hoe vergaat het de verliefde indiaan Adriaan? Wat maakt de leukste kok van Suriname klaar? Hoe ziet een school in Afrika er uit? Welke dieren wonen er in Indonesië? En wat krijg je in een Spaans restaurant op je bord? Rasentertainer Peer de Graaf en topgitarist Stefan Blankestijn laten kinderen horen en ervaren wat hun leeftijdgenoten in andere landen leuk vinden en met wat voor muziek zij daar groot worden. De Liedjesatlas is een interactieve en levendige muziekvoorstelling met door Koos Meinderts vertaalde liedjes. Een reis door de wereld!', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'035b19f8-c4d1-0272-0042-000000004161', N'Aisthetes, the one who perceives door Sulis_Vocaal ensemble. Renaissance muziek.', N'Begin dit jaar kwamen vijf amateur-zangers samen in de kunstacademie van Brugge voor een muzikaal avontuur. Het doel? Aantonen dat de muziek van William Byrd (1543-1623) kan gebracht worden door enthousiaste liefhebbers voor een gevarieerd publiek.  Openbaar repeteren op verschillende locaties lokte medereizigers. Het ensemble groeide en kreeg een instrumentale aanvulling met getalenteerde strijkers. Zo diende de mogelijkheid zich aan om de luister-ervaring meer toegankelijk te maken. Sulis blijft trouw aan de composities maar op basis van intuïtief aanvoelen wordt de muziek een dialoog tussen zangers en strijkers.  Deze intuïtie zou Sulis graag delen met de luisteraars. Bezoekers worden aangemoedigd om tijdens de concerten zelf creatief te zijn. Muziek en beeld gaan immers samen. Daarom krijgt het publiek de kans om de ervaring op tekenpapier te vertalen.   Een muzikale reis met Sulis wordt zo een artistieke totaal-ervaring voor alle metgezellen!', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-23T15:00:00.000' AS DateTime), CAST(N'2022-07-23T17:00:00.000' AS DateTime), N'e5bed21d-6539-40c7-90ec-281cb96cc55b')
GO
