INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'05952525-e0b9-414a-986d-97a7ab12c3b0', N'DACart Tentoonstelling', N'Na 2 jaar, mogen we weer! De kunstenaars van DACart popelen om hun creaties terug te tonen aan het grote publiek. DACart staat voor het artistiek-artisanale gedeelte van het dagactiviteitencentrum van Zonnehoeve|Living+, een vergunde zorgaanbieder voor volwassenen met een beperking. DACart  bestaat uit verschillende ateliers zoals schilderen, etsen, keramiek, lino, handwerk, tekenen, beeldhouwen, ... De begeleiders ondersteunen elke kunstenaar in diens creatief proces en dit vanuit een artistieke ontwikkeling. Beperkingen zijn er niet, enkel creatieve vrijheid. Doorheen het creatieproces komen individuele worstelingen, eigenheden, ... de unieke stijl van elke kunstenaar naar boven. Het resulteert in hoogstaande en unieke werken, die we graag aan u tonen tijdens onze jaarlijkse tentoonstelling op de Gentse Feesten.   Kom langs in de Reviszaal nabij het Baudelopark en leer de kunst en de kunstenaars beter kennen!', NULL, NULL, NULL, NULL)
