INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'0725747c-054e-400d-a31c-c52b59d70d50', N'Vlieg eens vlug - Bram Van Beusekom', N'Beeldend objecttheater over een jong vogeltje dat denkt dat hij niet kan vliegen. Lucky zit in zijn nest en fantaseert over hoe het zou zijn om lekkere vliegjes te vangen en met zijn vogelvriendjes naar de hoogste boom te vliegen. Maar wat als hij de enige is die niet kan vliegen? Dat hij te pletter zou vallen. Of erger, opgegeten door een kat…   Gelukkig leert de mama van Lucky hoe we hem kunnen aanmoedigen. Zo leren we hoe we allemaal onze vleugels uit kunnen slaan. Vlieg je mee?   ‘Vlieg eens vlug’ is een spannende en grappige voorstelling waarin versleten tuingereedschap als dieren tot leven komt. De bezems en laders uit het houten schuurtje van Bo de boswachter vormen het bos waarin we samen met de kinderen gaan proberen om Lucky te laten vliegen. Dans en zing mee en spreid je vleugels!', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
