INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'0769cb67-8759-0d59-7e03-000000001584', N'Lydia Ourahmane | Barzakh', N'De artistieke projecten van Lydia Ourahmane starten vaak bij gebeurtenissen uit haar persoonlijke omgeving of bij objecten die een spanning in zich dragen omwille van hun sociale, politieke of economische betekenis. Die objecten vormen de aanleiding voor diepgaand onderzoek naar de manier waarop herhaalde geschiedenissen van gedwongen verplaatsing en koloniale verdrukking zich inschrijven op individuele levens, lichamen en structuren.  Barzakh betekent, ‘het ongewisse’ in het Arabisch, de ‘tussen-staat’ tussen leven en dood, de plaats waar de geesten zich schuil houden maar ook een fysieke plek die bescherming biedt. Tijdens de lockdown bracht Ourahmane, omdat ze niet terug naar Algerije kon, haar ‘thuis’ tot bij haar in de vorm van een installatie met dezelfde oppervlakte en indeling als haar oorspronkelijke leefruimte. De inboedel verandert in een heel complexe, fragiele omgeving, die langzaam of onverwacht betekenissen vrijgeeft.', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-19T10:00:00.000' AS DateTime), CAST(N'2022-07-19T18:00:00.000' AS DateTime), N'a8f02e2f-b510-474b-80d0-59af8236221e')
