INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'08f0f07a-7623-aa46-24ea-000000002445', N'FA – Amoukanama Circus', N'Een groep individuen met verschillende achtergronden steken over naar een nieuwe bestemming. Een nieuw pad opent zich en de hoop groeit om diepe weerklank te vinden in een langverwachte nieuwe wereld. Wat kunnen we bereiken door samen te komen? Welke dromen kunnen uitkomen? Hoe ver kunnen we gaan buiten onze eigen grenzen?   In FA komen verschillende culturen en tradities tot leven, zowel acrobatisch, muzikaal als choreografisch.    Amoukanama is een Belgisch-Guinees collectief dat bruggen bouwt tussen Europa en Afrika via circus. Deze voorstelling vormde zich met 8 Guinese mannen en een Belgische vrouw.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-19T19:00:00.000' AS DateTime), CAST(N'2022-07-19T20:15:00.000' AS DateTime), N'24f2de57-3a6a-46a9-a8b6-660f4c4ca17b')
