INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'13dee832-75a0-40a5-a8f0-9fe96984a56b', N'Hocus Pocus Technolocus', N'Wetenschapsgoochelaar Marc Govaert trekt langs Vlaamse scholen met zijn show vol natuurkundige experimenten. Maar ook tijdens de Gentse Feesten wil hij bewijzen dat wetenschap alles behalve saai is. Uit zijn koffers vol huis-, tuin- en keukenspullen tovert hij experimenten die je zelf thuis kan doen en die je helpen de natuur te begrijpen. Een familievoorstelling waarbij je van de ene verbazing in de andere valt. Voor ‘curieuzeneuzen’ van 8 tot 88.', NULL, NULL, NULL, NULL)
