INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'169804de-8ee2-d807-3ef3-000000001629', N'Kom Wel', N'Het is een speciale dag. Thelma is jarig. Yihaa! Louise is jarig. Yihaa! En dat wordt gevierd! Alles is er: kroontjes, vlaggetjes, cadeautjes en taart. Nu wachten…  Ze komt wel. Op bezoek. Straks. Ze is zo welkom.  Een kleurrijke, surreële jazzy-country voorstelling over jezelf zijn, over samen en alleen, over wachten en nog eens wachten, over verjaren, over anders zijn en toch ook niet. En dat alles overgoten met een vleugje magie.  Van en met: Jits Van Belle, Tineke Caels & Stan Vangheluwe Coach: Frans Van der Aa Muziek: Stan Vangheluwe Decor: Kris Van Oudenhove Kostuums: Jana Roos Magic consultant: Jo De Rijck', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-16T16:00:00.000' AS DateTime), CAST(N'2022-07-16T17:00:00.000' AS DateTime), N'26f059a4-66c9-4581-a8e6-5d9b9f7ff469')
