INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'17520f43-8d5c-c5d4-3385-000000002439', N'On Point - Circumstances', N'Wie kan het langst blijven staan zonder zijn evenwicht te verliezen? Op de meest absurde, impressionante en gevaarlijke manier spelen twee acrobaten dit spel.  Je balans houden tussen, boven, onder, op, tegen elkaar. En vallen is geen optie.   Wat begint als een spel, ontwikkelt tot een gemeenschappelijke uitdaging. Elke stap, aanraking, verschuiving moet exact zijn. ON POINT. Ze worden volledig afhankelijk van elkaar… En komen daardoor ook gevaarlijk dichtbij elkaar en hun publiek. Wie houdt vol? En vooral: hoe lang?', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-18T20:00:00.000' AS DateTime), CAST(N'2022-07-18T20:30:00.000' AS DateTime), N'76fd4882-3301-43da-82a6-46eb71de5c42')
