INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'1b368345-a574-8b31-c2ca-000000003258', N'Sioen plays Graceland', N'In 1986 trok Paul Simon naar Zuid-Afrika voor de opnames van het album Graceland. Hij kwam terug met een legendarisch album en verbaasde vriend en vijand met een unieke sound. Het album bevat heel wat hits zoals You Can Call Me All’, ‘Diamonds On The Soles Of Her Shoes’, ‘Under African Skies’.  Meer dan 20 jaar later trok ook Sioen naar Zuid-Afrika voor de opnames van Calling Up Soweto. Hij werkte er samen met verschillende Zuid-Afrikaanse topmuzikanten.  Sioen, en de Zuid-Afrikaanse topmuzikanten die hij meebrengt, heeft in het verleden reeds bewezen dat hij Paul Simon alle eer aandoet. Op 21 juli herhaalt hij zijn straffe tour op de Korenmarkt.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-21T22:30:00.000' AS DateTime), CAST(N'2022-07-21T23:55:00.000' AS DateTime), N'008aed15-0846-45e8-a2ed-f64a75582a48')
