INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'1e9751d5-7376-42e8-bb8e-721482b2715e', N'Instaprondleiding door het Citadelpark', N'Het Citadelpark vertelt ons een resem aan grote en kleine verhalen. Het zijn stuk voor stuk herinneringen aan gebouwen, bezoekers, gebeurtenissen en tradities. De Gentse groene long kwam tot stand na de ontmanteling van het militair bolwerk dat onder het Nederlands bewind gerealiseerd werd in opdracht van Wellington, Brits strateeg en bevelvoerder. Toen in 1875 beslist werd om op de plaats van de Hollandse citadel een park aan te leggen ging, men er van uit dat net zoals de bomen en de struiken ook kunst en cultuur thuishoorden in deze groene oase.  Het park is voor vele Gentenaars, studenten en toeristen een fijne rustplaats waar onze gids An Hernalsteen je graag in meeneemt.', NULL, NULL, NULL, NULL)
