INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'212d10df-cee8-4681-b2a2-e9da66b88df4', N'Broer & Zus & Champignonsoep - Hilde Rogge', N'Thuis stinkt het. Hangt storing in de lucht. Broer en Zus gaan op zoek naar beter. Maar waar vinden ze dat ?  Als in een donker bos tovenarij hen achtervolgt, zijn ze half op weg… Maar er loert gevaar. Is er uitzicht ? Bestaat beter ?  Gelukkig is er … champignonsoep!  Deze muzikale vertelling is gebaseerd op een oeroud sprookje van Grimm. Over malheur en mazzel. Over een geluk bij een ongeluk. En over champignonsoep. Een vertelling vol vertedering, spanning en humor.', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
