INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'23804b2e-464b-7f61-296a-000000004773', N'Guy Verlinde Duo', N'Tijdens dit concert zal de focus liggen op de songs van zijn laatste album. Guy Verlinde kennen we als de energieke performer, maar hij laat zich steeds liever van zijn meest ingetogen kant zien. Het wordt een concert vol verhalen over liefde, verlies, hoop, wederopstanding … maar vooral over de positieve kracht van muziek. Een niet te missen concert met dromerige verhalende luisterliedjes die naar de keel grijpen.   MUZIKANTEN - Guy Verlinde: zang, gitaar, banjo - Olivier Vander Bauwede: harmonica, gitaar  Het afgelopen decennium domineerde Guy Verlinde de Belgische blues scene en toerde met zijn band “The Mighty Gators” non-stop door Europa. Hij nam in 11 jaar 12 albums op en speelde in verschillende prestigieuze clubs en op internationale festivals waar hij de affiche deelde met grootheden als: BB King, John Fogerty, Tony Joe White, Santana, John Hiatt, Canned Heat, Jeff Beck ... Guy laat zich bijstaan door de jonge klassemuzikant Olivier Vander Bauwede.', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-20T20:00:00.000' AS DateTime), CAST(N'2022-07-20T22:00:00.000' AS DateTime), N'8073e557-f829-43ec-b79b-b9093fd26b2b')
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'2398a6ae-274c-4f6a-997b-2cae5ebd1f66', N'Roodkapje en Wolf - De Vliegende Koffer', N'Over een meisje dat zich verveelt, een moeder die niet in sprookjes gelooft en een wolf die gelukkig niet op zijn eten kauwt. Het sprookje van Roodkapje in een frisse en sfeervolle vorm verteld en gespeeld door Charlotte de Lange.  Uit twee brocante koffers laat zij een betoverend bos, een spannend ledikant en een keuken waarin naar hartenlust gebakken wordt, verschijnen. In deze decors komen Roodkapje, Wolf en het koekdeeg als marionettes tot leven.', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
