INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'282e2f6f-0081-1f99-b1a1-000000001983', N'Instaprondleiding: LGBTQ+ en de MSK-collectie', N'Het MSK lanceert als eerste in België een LGBTQ+ rondleiding door de vaste collectiepresentatie. Via 11 kunstwerken staan we stil bij queer verhalen die letterlijk aan de MSK-muren hangen maar toch nog nooit verteld werden. We zien hoe uiteenlopend gender en geaardheid vroeger beleefd werden, en dat doet ons nadenken over hoe we hier vandaag naar kijken.  Scènes van de godin Artemis en haar vrouwelijke gezellen, de transformatie van Hermaphroditus, of het oudste MSK-portret van een ‘man die een relatie had met een man’: het zijn enkele van de stops in de kersverse tour. De rondleiding is samengesteld en gegeven door leden van de LGBTQ+ gemeenschap, maar iedereen is welkom om deze verborgen verhalen te ontdekken. We zien de rondeliding als een ''safe space'' met respect voor elkaars beleving en ruimte om visies te delen.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-24T14:00:00.000' AS DateTime), CAST(N'2022-07-24T15:30:00.000' AS DateTime), N'13e204b1-d1cb-411b-9515-9c492c510355')
