INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'2c0fcd79-e07c-43c6-aa74-070d2f24f4be', N'Kapitein Winokio', N'Al meer dan 15 jaar intussen vindt Kapitein Winokio liedjes en zingt ze samen met Mevrouw de Poes en zijn muziekmatrozen. Hij staat op – zingt een liedje, poetst zijn tanden – zingt een liedje, eitje ontbijtje – een liedje, varen – een liedje, … Voor àlles verzint onze Kapitein een melodietje. Kom, zing, dans, lach en vier met ons mee!  Nu en dan meert het schip van Kapitein Winokio aan voor onderhoud. Dan nemen Mevrouw de Poes en muziekmatrozen Ivanov en Petrolium de allerkleinste kleuters mee op avontuur. Spits dus de oren, smeer de stembanden en warm die beentjes maar op!', NULL, NULL, NULL, NULL)
