INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'2c39ce69-adfd-4ae5-81d2-814ea8ae2a18', N'Fietstocht ‘Kathedralen van de industrie’', N'Aan het einde van de 19e eeuw viert de Gentse textielindustrie nog hoogtij. Indrukwekkende fabriekscomplexen – stuk voor stuk architecturale pareltjes – verrijzen in het stadsbeeld. Maar vanaf de jaren 1960 keert het tij en in de daaropvolgende decennia brokkelt het grootse Gentse textielverleden letterlijk en figuurlijk af.  Fotograaf Alexander Dumarey herfotografeert vanaf 2020 tal van Gentse textielfabrieken die in de fotocollectie van het Industriemuseum te zien zijn. Herontdek via deze begeleide fietstocht de kathedralen van de industrie – of wat er nog van rest.', NULL, NULL, NULL, NULL)
