INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'2e9e9f0e-c5c1-4471-b488-b701bad53f91', N'Ouverture - Barokmuziek tijdens de Gentse Feesten', N'Nu we weer mogen, brengen ''The Ghent Baroque Players'' de Barokmuziek terug naar de Gentse Feesten. Het programma bestaat uit kamermuziek en ensemble muziek van onder andere Telemann, Bach, Pergolesi en Van Wassenaer.  7 strijkers, 3 blazers, 2 zangers en een Klavecimbel brengen het beste van zichzelf. Het concert is gratis maar u kunt een vrije bijdrage geven ten voordele van de vzw werkgroep vluchtelingen Gent en de vzw Thope, die rond huisvesting voor kansengroepen werkt. Er is een receptie na het concert.', NULL, NULL, NULL, NULL)
