INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'34197c8a-6fe0-421b-623d-000000002424', N'A 2 mètres – ADM vzw', N'Drie elementen. Twee acrobaten op een Chinese mast en zuurstof, het onzichtbare dat overal rondom ons hangt en toch ontbreekt in het lichaam van Jesse Huygh. Met hulp van Rocio laat Jesse zien dat we ons niet moeten laten leiden door angst maar door onze dromen. Zelf getroffen door mucoviscidose, toont Jesse de kracht van hoop.    A 2 mètres gaat over zichzelf ontdekken, elkaar helpen en leren om hulp vragen en kracht vinden in de kwetsbaarheid van ons bestaan. Een ontroerende voorstelling over grootmoedige momenten die mensen toestaan zich mens te voelen.    Vorig jaar speelde Jesse A 2 mètres nog tijdens Circus Cravings op Houtdok en was de voorstelling naar zorglocaties brengen nog een ultiem doel. Dit jaar helpen wij hem deze droom te realiseren.   Dit project werd ondersteund door het netwerk voor circus in openlucht, CIRC’UIT. Waar Miramiro trotste partner van is.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-16T14:30:00.000' AS DateTime), CAST(N'2022-07-16T15:10:00.000' AS DateTime), N'c250c0e6-8f8a-492f-a0f0-d2e37127a9bb')
