INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'412ca5c1-37f0-90ac-543d-000000004857', N'Echt Wel! - Koek en Ui', N'‘Echt wel!’ is een humoristische voorstelling over liegen, vriendschap en jezelf zijn. Voor kinderen vanaf 4 jaar. Sam is een echt liegbeest. Ze liegt over van alles. Eigenlijk spreekt ze zelden de waarheid.  “Ik ben in alle landen van de wereld geweest. Ik kan wel 10 meter hoog springen. Mijn scheetjes ruiken naar worteltjestaart. Ik ren sneller dan een cheeta. Ik ben familie van een koning. Ik heb gevochten met krokodillen. Ik ben de slimste persoon op aarde.”  Ze vertelt leugen na leugen. Maar is het wel verstandig om te liegen? En wat gebeurt er als je zo vaak liegt?  ‘Echt wel!’ is een humoristische voorstelling over liegen, vriendschap en jezelf zijn.', CAST(13 AS Decimal(2, 0)), CAST(N'2022-07-24T10:00:00.000' AS DateTime), CAST(N'2022-07-24T11:00:00.000' AS DateTime), N'73af1518-1923-420c-a8ad-950186ad6803')
