INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'42392550-02af-26c1-6ec1-000000003042', N'Gent op Wieltjes door een Roze Bril', N'Gent, toegankelijk voor iedereen.  Op vraag van de werkgroep ''andersvalide'' van ''Gent, vroeger en nu'' werkten we een stadsvertelling uit voor scootmobiels. De stadsgids is zelf mobiel tijdens de vertelling.  Een originele kennismaking met het Centrum en de Gentse Feesten zonder hindernissen, met aangepaste mogelijkheden voor een plasmoment en natuurlijk vergeten we een gezellig  pauze met een drankje en tapje niet.  12.50€ met een hapje en sneukel inbegrepen. Deelnemers regio Gent krijgen met Uitpas sociaal tarief korting.  Wie recht heeft op sociaal tarief met zijn Uitpas, maar buiten het Gentse woont, neemt best contact op per email met sociaaltoerisme @ gmail.com. We kijken dan om je ook een sociale korting te geven.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-22T14:30:00.000' AS DateTime), CAST(N'2022-07-22T17:00:00.000' AS DateTime), N'b55aed24-5d4b-47ec-b367-24f6d6fe6425')
