INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'435630ad-3291-4706-eaad-000000004155', N'Wirrewar - Efrem Stein', N'Dit is Meneertje. Dit is de rode draad waar het allemaal om draait. Meneertje volgt de rode draad. Stap voor stap. Binnen de lijntjes. Dag in. Dag uit. Elke week. Elke maand. Meneertje loopt zijn hele leven over hetzelfde lijntje. Zonder te spelen en zonder te lachen. Tot de dag dat hij….Spring, buk, draai en zoek. Meneertje gaat op onderzoek. ‘Wirrewar’ gaat over buiten de lijntjes durven kijken, over de noodzaak van spelen. Een grappige mimevoorstelling met minimale middelen en met grootse fantasie, speciaal voor peuters en kleuters!', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-16T09:45:00.000' AS DateTime), CAST(N'2022-07-16T10:30:00.000' AS DateTime), N'd61019a2-60f7-4ad6-ac76-1815cf7f8b58')
