INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'45a070c8-ca49-7c6c-ba99-000000001365', N'DACart Tentoonstelling', N'Na 2 jaar, mogen we weer! De kunstenaars van DACart popelen om hun creaties terug te tonen aan het grote publiek. DACart staat voor het artistiek-artisanale gedeelte van het dagactiviteitencentrum van Zonnehoeve|Living+, een vergunde zorgaanbieder voor volwassenen met een beperking. DACart  bestaat uit verschillende ateliers zoals schilderen, etsen, keramiek, lino, handwerk, tekenen, beeldhouwen, ... De begeleiders ondersteunen elke kunstenaar in diens creatief proces en dit vanuit een artistieke ontwikkeling. Beperkingen zijn er niet, enkel creatieve vrijheid. Doorheen het creatieproces komen individuele worstelingen, eigenheden, ... de unieke stijl van elke kunstenaar naar boven. Het resulteert in hoogstaande en unieke werken, die we graag aan u tonen tijdens onze jaarlijkse tentoonstelling op de Gentse Feesten.   Kom langs in de Reviszaal nabij het Baudelopark en leer de kunst en de kunstenaars beter kennen!', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-18T15:00:00.000' AS DateTime), CAST(N'2022-07-18T22:00:00.000' AS DateTime), N'05952525-e0b9-414a-986d-97a7ab12c3b0')
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'45b47bb8-8cf0-d1b5-0860-000000000855', N'Faust ofte krakeling beneden de louteringsberg', N'Pjeroo Roobjee bewerkte deze klassieker naar -in zijn woorden- een représentation waarin hemel en helle vergaan, waarin ieder verdwijnt en verschijnt in schoon en ander kostuums en het hellevuurwerk vol sulfer en pek en schoon blommekees aan het verbouwereerde publiek wordt voorgeschoteld! Dat allemaal in een decorke van grootse kastelen en uitgestrekte velden, bossen, hemelse kamers, krochten van prison, en hellevuur waar ge u wreed aan brandt vol pijn en alteratie.  Over lust en liefde (of wat daarvoor moet doorgaan), over liegen en bedriegen en moord en kindermoord en andere leuke dingen. Enfin, over de couleuren van het leven.  Tekst: Pjeroo Roobjee - Regie: Etienne De Ruyck - Spel: Lynn Ryssen, Pieter Theunissen, Heleen Van Melckebeke, Leander Vergote - Productieassistentie: Leena Van den Bergh - Soundscape: Joris Van Maldeghem - Decor: Jonas Dusselier - Foto: Jean-Pierre Fack', CAST(12 AS Decimal(2, 0)), CAST(N'2022-07-21T20:00:00.000' AS DateTime), CAST(N'2022-07-22T05:59:59.000' AS DateTime), N'5a65bece-4082-45bf-a5b4-7ef611ad6edd')
