INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'488d77fb-76d8-4342-b56a-f5496a48914a', N'Gentse Buitenband - startplaats Zwijnaarde', N'De Gentse Buitenband is fietsen, wandelen en feesten in de groene gordel rond Gent.  Kies uit routes langs de mooie fietspaden in het landelijke groen dat Gent omringt. Geniet op onze startplaatsen, stuk voor stuk echte feestlocaties met hapjes, drankjes en animatie voor jong en oud. Starten kan doorlopend tussen 8u en 15u.  Voorinschrijvingen: €3, dag zelf: €5. Kinderen (tot 12j): gratis.  Meer info en hoe inschrijven, vind je op onze website.   Door vooraf in te schrijven, maak je bovendien kans om een fiets te winnen, geschonken door BIKER Fietsen. Hou zeker ook onze facebookpagina in de gaten voor de laatste nieuwtjes.  8km: Voedselboswandeling 25km: Korte Keten route 45km: Gentse Kweek tussen Leie en Schelde 80 km: Gentse Buitenband (voor wielertoeristen) Maaltijd (12-14u): Foodtruck Meer info: 0486 53 97 81', NULL, NULL, NULL, NULL)
