INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'4a09679c-ca57-44d0-a7ab-58035f3fd100', N'Rondleiding: De Waterwijk, een vergeten Patershol?', N'De waterwijk toont een mix van textielarbeiders, rijke kantexporteurs, brouwers naast het alomtegenwoordige Baudeloklooster.  Er zijn geen grote monumenten, geen Middeleeuwse stenen te vinden, omdat de stad zelf de gronden in eigendom hield. De nabijheid van de Vrijdagmarkt, centrum van de linnenhandel, brengt al vroeg vanaf de 17de eeuw textielnijverheid, vooral linnen, in de wijk. De aanwezigheid van het MIAT bevestigt dit vandaag nog. In sommige straten woonden vooral arme arbeiders. In andere straten leidde de export van kant tot  grote rijkdom en dat contrast is er tot vandaag zichtbaar. Door het vele water vestigen zich bovendien veel brouwerijen in de wijk, vooral langs de Steendam, waar vandaag nog 18de eeuwse woningen herinneren aan hun voorspoed. Wanneer uiteindelijk de industrie uit het centrum verdwijnt, verdwijnen ook de beluiken. In hun plaats komen statige burgerwoningen, waar de Baudelostraat van getuigt.', NULL, NULL, NULL, NULL)
