INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'4d7d5167-a97e-781e-aa3b-000000004803', N'Hap uit je bed - Bram Van Beusekom', N'Een energieke en humoristische voorstelling over expres verdwalen in je eigen fantasie. Bedtijd! Niek en Isa moeten gaan slapen. De dekens liggen precies recht, de gordijnen zitten netjes dicht en de wasmand staat gewoon in de hoek. Check! Alles is precies wat het is dus het nachtlampje kan uit.  Huh, dat lampje kijkt me aan. Nee, dat kan niet. Dansen de dekens nou wervelend door de kamer? Mijn kussen rent opeens kwispelend achter jouw tandenborstel aan. Au, die wasmand bijt in m’n bil. Vlug, spring op mijn deken dan varen we weg!  Ben jij klaar voor het grootste avontuur dat je zelf kunt verzinnen?   Hap uit je bed is een beeldende voorstelling vol acrobatiek en poppenspel voor alle kinderen tussen de 4 en 8 jaar.  Waarin de kinderlijke verbeelding zó sterk is, dat zelfs volwassenen het gaan zien!', CAST(12 AS Decimal(2, 0)), CAST(N'2022-07-22T10:00:00.000' AS DateTime), CAST(N'2022-07-22T11:00:00.000' AS DateTime), N'633d16f3-3ed1-4921-ab0e-ab8ae2be4aa8')
