INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'4fd50790-efe2-4481-bf42-bfac71d53c8f', N'Concert Anton Skakun', N'Anton Skakun is een Oekraïense violist-virtuoos. Hij treedt regelmatig op als solist, speelt in vele kamermuziekensembles en is evenals orkestmusicus. Anton Skakun werd in 1984 geboren in Kiev (Oekraïne) in het muzikale gezin van de bekende Oekraïense dirigent, koormeester, pedagoog en componist Vitalyi Skakun.Tijdens zijn opleiding in Oekraïne en Zwitserland soleerde Anton regelmatig met verschillende orkesten, onder meer de orkesten van Poltava, Kiev, Zaporizhzhya, Kharkiv, Lviv, Freiburg alsook het orkest van het Sydney Conservatorium of Music. Sinds 2014 is Anton vaste deelnemer van het festival Kiev, Music Fest, en trad hij als solist op in de Victoria Hall in Genève. Op 12 februari 2020 soleerde hij in het Tripelconcert van Beethoven met het Kharkiv Philharmonic Orchestra. Reserveren niet nodig.', NULL, NULL, NULL, N'09853ead-9a4d-439f-b376-1a188f339a64')
