INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'5034c33c-1b95-fb39-dff2-000000001164', N'Pippa & de Voelboel', N'Pippa & de Voelboel nemen jou mee op een zoektocht vol kleur, muziek, humor, dans en gevoel!  Interactief muziektheater voor kinderen van 4-6 jaar waarbij je gewoon niet stil kan blijven zitten!  Pippa voelt zich vandaag zo grijs. Niet blauw, niet rood, niet groen en ook niet geel, maar grijs!  Niets voelen vindt Pippa helemaal niet leuk. Want zonder droevig, bang en boos is ook blij er niet meer bij… Zal Pippa er in slagen om haar gevoelens terug te vinden?', CAST(12 AS Decimal(2, 0)), CAST(N'2022-07-17T11:00:00.000' AS DateTime), CAST(N'2022-07-17T11:45:00.000' AS DateTime), N'065a7671-9dbc-4361-8da3-219d3845a752')
