INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'518e0454-4d17-71f8-dae8-000000004380', N'4T4', N'"Kristof “4T4” Michiels is geboren als muziekfanaat en heeft de evolutie van elektronische dansmuziek steeds vanop de eerste rij beleefd;  zijn carrière overspant 4 (!?!?) decennia dankzij een zeer vroege start als dj in het Hasseltse “’t Kelderke”. Hoewel er nooit sprake is geweest van een internationale doorbraak, is hij één van de meest gerespecteerde en ontzagwekkende dj’s in de Belgische scene - hij mag gerust een begrip worden genoemd. We kennen hem misschien vooral als 1 van de 3 leden van ’t Hof Van Commerce, maar ook van de vele parties die hij al in goede banen mocht leiden, of als maker van de Canvasreeks Platendraaiers. Hij staat bekend als bruggenbouwer tussen een zeer uiteenlopende resem stijlen en culturen, en zijn skills liegen er ook niet om. Na een welverdiende pauze van enkele jaren is hij nu weer terug om ons eraan te herinneren wie de vibemaster is, en loodst hij ons moeiteloos doorheen 40 jaar dansvloerparels van alle soort. Zweten en boenken!"', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-20T01:30:00.000' AS DateTime), CAST(N'2022-07-20T05:59:59.000' AS DateTime), N'8f0f9994-a4ca-469a-91ab-e739b4282bc4')
