INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'52f87c0d-cac2-8bff-e2f1-000000001827', N'The Calicos', N'The Calicos, winnaar van Humo’s Rock Rally in 2018, scoorden in de zomer van 2020 hun eerste hit met de single ‘Nova’. Een sierlijke en epische oorwurm die het onder andere tot #1 in de VOXlijst van Radio 1 schopte en de landing van de langverwachte debuutplaat inzette.  In mei 2021 kwam The Soft Landing uit bij Mayway Records. Met daarop 9 diverse songs die het spectrum tussen americana en indierock bestrijken en samen een homogene, warme plaat vormen. Grote namen als Wilco, Ryan Adams, Jonathan Wilson en The War On Drugs zijn nooit ver weg, maar The Calicos beschikken ondanks hun nog jeugdige leeftijd al over het metier om uit die rijke invloeden een zeer eigen sound te puren.  De band spreekt een breed muziekminnend publiek aan en is uitgegroeid tot een alternatieve popgroep die generaties kan verzoenen.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-18T23:15:00.000' AS DateTime), CAST(N'2022-07-19T05:59:59.000' AS DateTime), N'760689b7-bb19-431c-8ebe-af231ce9f88f')
