INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'552f671b-812e-7655-953e-000000003270', N'Tentoonstelling Tussen Ergens & Nergens', N'"Hier, achtergelaten, gebruikt of in gebruik, een matras op het voetpad, naast een station, of op een onwaarschijnlijke, troosteloze plek.  Graffiti op kille muren, eindeloze gevels. Buiten is het koud, ijzig koud, of warm, ondraaglijk warm. Als je je ogen sluit, waan je je ergens anders en droom je je tussen ''ergens en nergens'' even weg. Philip Marquebreuck ontwikkelt een fotoreeks met achtergelaten matrassen, onderkomens van wie op weg is van ergens naar nergens;  stille foto’s, gemaakt van op afstand, ze pakken de kou, de ruwheid, de hardheid maar ook de schoonheid van mens en stad.  Wendy Krochmalonderzoekt met haar keramieken sculpturen de vlucht van ''waar naar daar'', versteende gedachten met respect voor de geschiedenis en een blik op de toekomst."', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-24T12:00:00.000' AS DateTime), CAST(N'2022-07-24T17:00:00.000' AS DateTime), N'b1d4873e-dc80-4b03-98da-2ddb1f07d021')
