INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'56293992-13a8-4e9d-bcba-d931c9b8bcbd', N'De Wraak Van Lorre - Epos Ensemble', N'Een huisdier hebben is leuk, maar een prachtig opera-zingende papegaai als huisdier hebben is helemaal fantastisch! Jop is dan ook helemaal in de wolken als Lorre bij hem komt wonen en hij kan al gauw niet meer zonder het lieve beestje. Maar helaas is Jop niet de enige die wat in Lorre ziet: ook meneer Panini wil hem graag hebben om héél veel geld aan hem te verdienen in de grote operazalen wereldwijd. De geniepige Panini haalt de raarste streken uit om Lorre van Jop af te pakken, en dát laten Jop en zijn vrienden niet zo maar op zich zitten...  Tosca Menten schreef het boek waar dit verhaal op gebaseerd is, en het ensemble maakte er een eigenzinnige versie van vol theatrale en interactieve elementen. Ook de kinderen zelf zullen aan het zingen gezet worden!', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
