INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'566d9dc6-7774-2476-559d-000000001926', N'Stok Paard Prins', N'Stok Paard Prins haalt zijn houten ros weer van stal en gaat op queeste naar de ware liefde.  Een sprookjesachtige en humoristische buitenvoorstelling over avonturen beleven, proberen, falen en opnieuw proberen. Over zoeken naar liefde. En die vinden op een plek waar je het eerst niet had verwacht!  Tekst: Raf Walschaerts en Lander Severins Regie: Raf Walschaerts Spel: Lander Severins of Myrthe Van Velden of Loes Carrette of Thomas Van Caeneghem Stem: Raf Walschaerts Muziek: Thomas Van Caeneghem Kostuums: Jana Roos In samenwerking met: Historische Huizen Gent', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-19T11:00:00.000' AS DateTime), CAST(N'2022-07-19T11:45:00.000' AS DateTime), N'0403a228-9fdb-4ecc-ab26-77862b77ce5c')
