INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'59f1c687-8c0c-4783-bbf3-3c0ef2d4f9ad', N'Rondleiding: Sterke Gentse vrouwen', N'Proviandiersters, koopwijven, devotarigghen, nonnen, studentes, onderwijzeressen, en zelfs de Maagd van Gent. Gent zou er zonder sterke vrouwen helemaal anders uitzien.  Achter het zinnebeeld en wapenschild van de stad, de Maagd van Gent, schuilt een ongelooflijke rijkdom aan vrouwelijke schoonheid, intellect, historische inzet, devotie, sociaal barricadenwerk en dergelijke meer. De wandeling geeft als opzet een schets van het leven van de modale Gentse vrouw, van de middeleeuwen tot in het interbellum, en niet zozeer een opsomming te geven van de grote Gentse vrouwen, waarvan er slechts enkele ter sprake zullen komen.  De strijd om emancipatie, de langzame opstanding van de Gentse vrouw in de twintigste eeuw, haar lot en inzet tijdens de Eerste Wereldoorlog en andere weinig gekende gegevens komen aan bod tijdens deze wandeling. Elk verhaal, elke historisch verantwoorde anekdote wordt telkens geplaatst tegen de achtergrond van het op vele plaatsen in de stad aanwezige vrouwbeeld.', NULL, NULL, NULL, NULL)
