INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'5a65bece-4082-45bf-a5b4-7ef611ad6edd', N'Faust ofte krakeling beneden de louteringsberg', N'Pjeroo Roobjee bewerkte deze klassieker naar -in zijn woorden- een représentation waarin hemel en helle vergaan, waarin ieder verdwijnt en verschijnt in schoon en ander kostuums en het hellevuurwerk vol sulfer en pek en schoon blommekees aan het verbouwereerde publiek wordt voorgeschoteld! Dat allemaal in een decorke van grootse kastelen en uitgestrekte velden, bossen, hemelse kamers, krochten van prison, en hellevuur waar ge u wreed aan brandt vol pijn en alteratie.  Over lust en liefde (of wat daarvoor moet doorgaan), over liegen en bedriegen en moord en kindermoord en andere leuke dingen. Enfin, over de couleuren van het leven.  Tekst: Pjeroo Roobjee - Regie: Etienne De Ruyck - Spel: Lynn Ryssen, Pieter Theunissen, Heleen Van Melckebeke, Leander Vergote - Productieassistentie: Leena Van den Bergh - Soundscape: Joris Van Maldeghem - Decor: Jonas Dusselier - Foto: Jean-Pierre Fack', NULL, NULL, NULL, NULL)
