INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'5ae81f4b-0df0-482c-9783-b38cd25c5906', N'Concert Consorto Etereo', N'Het oudemuziekensemble Consorto Etereo werd door Jole De Baerdemaeker, Adelheid Glatt, Johanna Lambrechts en Liam Fennelly opgericht. Hun passie voor de historische uitvoeringspraktijk en het musicologisch onderzoek bracht hen samen en resulteerde in een dynamisch ensemble dat, naargelang het repertoire, met wisselende bezettingen voor de dag komt. Voor dit concert nemen ze twee muzikale generaties onder de loep, om de evoluties binnen de rijke Franco-Vlaamse polyfone traditie in de verf te zetten. Hiervoor gingen ze snuisteren in de vele chansons die voor Margaretha van Oostenrijk en later voor haar neefje Karel V geschreven werden. Na vele intense repetities presenteren zij u een gevarieerd programma van drie- en vierstemmige polyfonie uit de chansonniers van het Mechelse hof van Savoye en het Gentse Prinsenhof.  Sopraan is deze keer Emma Coopman. Tickets bestellen bij consorto-etereo@telenet.be', NULL, NULL, NULL, N'09853ead-9a4d-439f-b376-1a188f339a64')
GO
