INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'5b3c301d-7650-b714-da61-000000004530', N'Plons - Theater Smoespot', N'"Over een kleine kikker, die dapper genoeg is om haar hoofd boven water te steken.  In Plons volgen we kikker Kiki, die geboren wordt als klein kikkervisje. Langzaam ontdekt Kiki de wereld om haar heen en raakt verwonderd door de schoonheid van de vijver. Een grote vis lijkt even heel gevaarlijk, maar blijkt haar te kunnen helpen. Kiki ziet de libelle die naar werelden kan gaan, waar onze jonge kikker nog niet is geweest. Kiki verandert, wordt groter, net als de wereld om haar heen. Het is wel lekker veilig in de vijver, maar als zij straks aan land gaat, wat zal er dan gebeuren?  Kiki is een beetje bang. Toch springt ze uit de vijver en gaat aan land. Hier ontmoet zij bewoners die haar plagen of vrienden met haar willen worden. Onder andere de kokerjuffer, die zich langzaam opsluit in haar koker. Waar ze dan later als een schietmot uit tevoorschijn komt.  Er zijn ook minder leuke vrienden;  zoals de reiger die kikker wel een lekker hapje vindt. Wegwezen!"', CAST(13 AS Decimal(2, 0)), CAST(N'2022-07-18T15:00:00.000' AS DateTime), CAST(N'2022-07-18T16:00:00.000' AS DateTime), N'7f99955b-0918-4f96-b68b-2e11a68fcc78')
