INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'5c475b02-1ad4-4020-ade2-e24c01d9eb14', N'55/91', N'55 is geïnspireerd door de gele schilderijen van Francis Bacon, die fungeerden als moodboard om de schilderijen als het ware tot levende cinema te brengen. 55 zuigt het publiek binnen in een ruimte waarin een energie heerst. De energie is geel maar niet in de betekenis van happy. Zoals in Bacons schilderijen is de ruimte gesloten en claustrofobisch. In deze wereld schept de waarnemer zijn eigen solipsistisch universum. Enkel de eigen geest bestaat en creëert. Oranje is de pivoterende kleur die in 91 de temperatuur aangeeft. Het is de kleur tussen uitersten, steeds in transitie en daarom onvatbaar. 91 is ook geïnspireerd door de schilderijen van Francis Bacon, maar dan wel door de oranje werken.', NULL, NULL, NULL, NULL)
GO
