INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'5d6a71ef-2d5e-ff4b-6409-000000005040', N'WORKSHOP IMPROVISATIETHEATER - SAAIHEID TROEF (Alles kapot Festival)', N'"Door: Pieter Beck: improvisator bij FUS! en Caque.  Een sterke keuze! Een goei personage! Origineel zijn! Vaak zijn improvisatietheaterspelers over-the-top opgewonden en energiek en zien ze niet wat de medespeler doet, zegt of is. In deze workshop ga je niet interessant zijn, maar probeer je ""nog saaier"" te zijn. Vanuit deze saaiheid, neutraliteit of niksende openheid kom je vanzelf tot originele, rakende en leesbare scènes. We poneren niet dat er geen sterke keuzes zouden mogen gemaakt worden, maar nu even niet.  (www.fusimprov.be)"', CAST(30 AS Decimal(2, 0)), CAST(N'2022-07-24T14:00:00.000' AS DateTime), CAST(N'2022-07-25T05:59:59.000' AS DateTime), N'31788818-6b89-46f5-922e-fc4495fc3f8e')
