INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'618882ee-3924-417c-8733-0d3218ebeb26', N'EXIT - Circumstances', N'Vier individuen bewegen zich doorheen een grote, indrukwekkende installatie met een draaiende muur en zwevende deuren. Ze komen toe en vertrekken, verschijnen en verdwijnen. Deze voorstelling toont hun ontmoeting wanneer ze één collectief pad belopen.      EXIT is een spannende, impressionante en fysieke circus- en dansvoorstelling waar beweging, partneracrobatie en balansbord samenvloeien.   Dit project werd ondersteund door het netwerk voor circus in openlucht, CIRC’UIT. Waar Miramiro trotste partner van is.', NULL, NULL, NULL, NULL)
