INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'62fc70df-601e-5db0-e604-000000005454', N'La voix humaine - OPERAWERF113', N'Het jonge muziektheatergezelschap OPERAWERF113 brengt La voix humaine, een kleurrijk monodrama van de Franse componist Francis Poulenc. De telefoon gaat. Een vrouw neemt op. Verkeerd verbonden? Of haar ex-geliefde? In het begin houdt de vrouw zich sterk, vertelt honderduit. Naarmate de avond vordert, blikt ze echter steeds dieper in de afgrond. Sopraan Liza Dedapper vertolkt de hoofdrol, begeleid door pianist Jelle Vastershaeghe en een regie van Mien Bogaert. Gezongen in het Frans, met Nederlandse boventitels.', CAST(18 AS Decimal(2, 0)), CAST(N'2022-07-17T19:30:00.000' AS DateTime), CAST(N'2022-07-17T20:30:00.000' AS DateTime), N'f62a29e9-150b-4023-b654-8efd0e1fd0c2')
