INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'65a6c4fa-bdd3-4351-b10d-aa50d70ec5d2', N'N. Dash', N'De werken van N. Dash ontstaan vanuit een intense en gelijkwaardige uitwisseling met wat nabij is. In haar veelzijdige praktijk verwerkt de kunstenaar nederige materialen zoals vuil, aarde, jute, touw, reepjes piepschuim, plastic flessen en karton tot complexe composities. Ze ontleent daarbij methodes en substanties aan de schilderkunst, beeldhouwkunst, fotografie en drukkunst. Via deze transformaties lijkt er zich een paradox te vormen bij de beleving van de werken, die zowel verfijnd als ongecompliceerd aandoen. De vitaliteit en zuinigheid van de constructies zijn te herleiden tot haar verbondenheid met de omgeving, zowel natuurlijk als geconstrueerd, en met de geologische tijd of het enorme interval waarin biologisch leven op aarde is ontstaan. Het tactiele werk vertoont een onderliggende spanning tussen wat industrieel ‘gegeven’ en wat manueel gemaakt is, tussen de onvoorspelbaarheid van de elementen en de menselijke pogingen om deze natuurlijke energieën te beheersen.', NULL, NULL, NULL, NULL)
