INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'687c5cbd-9147-c3c8-a923-000000001812', N'De Flandriens ‘de Popklassieker’', N'Uit het rijke repertoire van pakweg 60 jaar Vlaamse en Belgische popgeschiedenis maken de Flandriens een eigenzinnige keuze. Zij vertalen de Engels- en Franstalige hits in het Nederlands. Of het West-Vlaams. Of zelfs in het Aalsters. Zonder vooroordelen nemen ze alle genres onder handen, van disco tot punk, van dance tot chanson. Vele hits van onder andere The Pebbles, Hooverphonic, Vaya Con Dios, Stromae, Technotronic, Plastic Bertrand, Axelle Red, maar ook iets meer vergeten parels uit de Vlaamse muziekschatkamer. Allemaal door de Flandriens heruitgevonden in het Nederlands en in een spetterend modern jasje gegoten. De Flandriens: een stomende liveband met maar liefst vier zangers. De muzikanten schreven elk afzonderlijk Vlaamse popgeschiedenis. Met de Flandriens doen ze het samen, onder de deskundige leiding van Dirk Blanchart. Verwacht geen evidente songs en nog minder evidente uitvoeringen. De Flandriens doen hun eigen ding met de hits uit het verleden. Koppig en eigenzinnig …', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-17T23:15:00.000' AS DateTime), CAST(N'2022-07-18T05:59:59.000' AS DateTime), N'cd19302a-a063-4476-ab32-2f62579eba4a')
