INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'69f36424-c678-46bb-8d9c-25623a148300', N'WORKSHOP: Vind je spot! Musical Improtheater op Alles Kapot Festival', N'Door: Bart Bracke, improvisator bij Caque & FUS!, en grote fan van Musical Improv. Samen met pianist-improvisator Peter Van Riet, bekend van Peter Van Riet. ----------- De moeilijkste stap bij een lied improviseren in improvisatietheater. Wat ga ik daar eigenlijk doen vooraan? En over wat gaan we dan wel zingen? En wat als het publiek het afgrijselijk vindt? Deze workshop gaat niet naar alle extraatjes en vrolijke vetrandjes die we met een geïmproviseerde musical associëren, zoals rijmen, zangacrobatie of choreo. Deze workshop gaat naar zijn naakte essentie: iemand die vol emotie de stap zet in de spotlight. Omdat niet zingen geen optie meer is.', NULL, NULL, NULL, NULL)
