INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'6ac6f7e7-136d-3f89-0a94-000000005379', N'Djiezes! Lieven Tavernier Aperitiefconcert', N'Vanuit de Sint-michielsbeweging organiseren we opnieuw ons Djiezes! festival tijdens  de Gentse Feesten (15-17 juli) in ons huis en tuin in de Lange Violettestraat 277! Niet te missen!  - Vrijdagavond (15/7, 19u30-22u): open Taizé-gebedsavond gevolgd door lekkere, huisgemaakte spaghetti aan studentikoze prijs en Volksspelen.  - Zaterdag (16/7): Gentse Brunch (8u30-11u) boordevol Gentse specialiteiten en streekproducten! Aansluitend vrij toegankelijke aperitiefconcerten (12u-16u) door bekende en minder bekende namen uit de Gentse (studenten) music scene oa. Lieven Tavernier! - Zaterdagavond (16/7, 17u-18u) verzorgen we ook een Gospel Eucharistieviering in de begijnhofkerk met aansluitend een Orval tasting in het huis (18u15).  - Zondag (17/7): Gentse Brunch (8u30-11u) boordevol Gentse specialiteiten! Aansluitend vrij toegankelijke aperitiefconcerten (12u-16u) met oa. Roel Van Bambost!  Brunch tickets (20€ in VVK) zijn te verkrijgen via karel.sintmichiel@gmail.com', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-16T12:00:00.000' AS DateTime), CAST(N'2022-07-16T14:00:00.000' AS DateTime), N'02296fc5-9a95-4711-a0f4-920149ec34fb')
