INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'6adf9f9b-ccc8-f403-aee0-000000004077', N'Mama''s Jasje', N'"2020 was het jaar van de grote comeback van Mama’s Jasje, de band rond zanger Peter Vanlaet. Zo was er de succesvolle passage in Liefde Voor Muziek en het album ''30 jaar – De Jaren Van Verstand'' was goed voor goud. Begin 2021 werd de band uitgenodigd door radiozender JOE om wekelijks een cover van een hit uit JOE’s Top 2000 te brengen. Deze coverversies, aangevuld met eigen werk, werden gebundeld op het nieuwe album ''Spiegel'', dat in de hoogste regionen van de hitlijsten terug te vinden was. En de uitverkochte jubileumtournee ''30 jaar Mama’s Jasje'', die heel wat theaterzalen aandeed, was een laaiend succes! Peter Vanlaet en zijn band lieten er op meesterlijke wijze de ene onsterfelijke klassieker na de andere door de boxen knallen. ""Zo Ver Weg"", ""Een Teken Van Leven"", ""Doe Het Licht Maar Uit"", ""Als De Dag Van Toen"" … het publiek had aan een half woord genoeg om ze uit volle borst mee te zingen."', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-17T22:30:00.000' AS DateTime), CAST(N'2022-07-17T23:55:00.000' AS DateTime), N'816efa3c-6e2c-4231-b538-4587344b7f43')
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'6ae0277f-f741-374c-cf59-000000004104', N'Ouverture - Barokmuziek tijdens de Gentse Feesten', N'Nu we weer mogen, brengen ''The Ghent Baroque Players'' de Barokmuziek terug naar de Gentse Feesten. Het programma bestaat uit kamermuziek en ensemble muziek van onder andere Telemann, Bach, Pergolesi en Van Wassenaer.  7 strijkers, 3 blazers, 2 zangers en een Klavecimbel brengen het beste van zichzelf. Het concert is gratis maar u kunt een vrije bijdrage geven ten voordele van de vzw werkgroep vluchtelingen Gent en de vzw Thope, die rond huisvesting voor kansengroepen werkt. Er is een receptie na het concert.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-19T20:00:00.000' AS DateTime), CAST(N'2022-07-19T22:00:00.000' AS DateTime), N'2e9e9f0e-c5c1-4471-b488-b701bad53f91')
