INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'6c16017d-a28b-963e-e435-000000001173', N'Iedereen Moet Leven', N'Iedereen moet leven, wat er ook gebeurt. Iedereen moet leven, hoe het ook draait of keert of staat of valt, iedereen moet leven. Waarom ook niet? Wat zouden we anders doen?   Weet er iemand werkelijk nog wat er moet, mag of beter niet of wel gebeurt? Hoe lang kunnen we weten wat we weten voor het achterhaald is? Denken we niet te veel te weten?  Gaan we wijzer worden door naar ‘Iedereen Moet Leven’ van Arno Moens te gaan? Wie weet. Zal de titel minder vaak herhaald worden op de planken? Sowieso. Maar kijk, iedereen moet leven, of we dat nu lezen of horen, vergeten gaan we het niet meer doen.', CAST(10 AS Decimal(2, 0)), CAST(N'2022-07-19T17:00:00.000' AS DateTime), CAST(N'2022-07-19T18:15:00.000' AS DateTime), N'63800578-9b49-4d12-9eaa-9c9565a175c9')
