INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'6cf6b5bb-14aa-4df1-9863-b12d69a4a651', N'Ollie Trollie reist naar de sterren', N'"Ollie Trollie wil dat zijn opa Bollie Trol met hem speelt. Maar opa is druk met zijn grote liefhebberij;  kijken naar de sterren en de maan. Ollie loopt mopperend weg. Dan ontdekt hij tot zijn grote schrik dat zijn opa er niet meer is. Waar is opa Bollie Trol? In de ruimte? Tussen de sterren? Op de maan? Lukt het Ollie om zijn opa te bereiken en het weer goed te maken?  Ollie Trollie maakt een spannende reis, natuurlijk samen met zijn maatje Vleermuis Vlerk én met nieuwe vrienden die ze onderweg ontmoeten. Sfeervol en humoristisch poppentheater met mooie vilten poppen, die voor herkenning en een vleugje ontroering zorgen."', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
