INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'731a684c-69d6-8543-b480-000000002688', N'Workshop: word poppenspeler in het Huis van Alijn!', N'Elaba schroeleboelekes! Zin om ook poppenspeler te worden? Tijdens de Gentse Feesten leren de poppenspelers van Pierke van Alijn je de kneepjes van het vak. Tijdens een workshop ontdek je hoe poppen in een theatervoorstelling tot leven komen. Je leert ze stappen, spreken en het liedje van Pierke zingen.  Op het einde zijn je (groot)ouders welkom voor een toonmoment met Pierke in de hoofdrol.', CAST(10 AS Decimal(2, 0)), CAST(N'2022-07-23T10:00:00.000' AS DateTime), CAST(N'2022-07-23T11:30:00.000' AS DateTime), N'b0e4dd2e-4259-4b18-88c4-d0b1d1503ef7')
