INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'73af1518-1923-420c-a8ad-950186ad6803', N'Echt Wel! - Koek en Ui', N'‘Echt wel!’ is een humoristische voorstelling over liegen, vriendschap en jezelf zijn. Voor kinderen vanaf 4 jaar. Sam is een echt liegbeest. Ze liegt over van alles. Eigenlijk spreekt ze zelden de waarheid.  “Ik ben in alle landen van de wereld geweest. Ik kan wel 10 meter hoog springen. Mijn scheetjes ruiken naar worteltjestaart. Ik ren sneller dan een cheeta. Ik ben familie van een koning. Ik heb gevochten met krokodillen. Ik ben de slimste persoon op aarde.”  Ze vertelt leugen na leugen. Maar is het wel verstandig om te liegen? En wat gebeurt er als je zo vaak liegt?  ‘Echt wel!’ is een humoristische voorstelling over liegen, vriendschap en jezelf zijn.', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
