INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'73f01aeb-e852-1215-189f-000000004791', N'Roodkapje en Wolf - De Vliegende Koffer', N'Over een meisje dat zich verveelt, een moeder die niet in sprookjes gelooft en een wolf die gelukkig niet op zijn eten kauwt. Het sprookje van Roodkapje in een frisse en sfeervolle vorm verteld en gespeeld door Charlotte de Lange.  Uit twee brocante koffers laat zij een betoverend bos, een spannend ledikant en een keuken waarin naar hartenlust gebakken wordt, verschijnen. In deze decors komen Roodkapje, Wolf en het koekdeeg als marionettes tot leven.', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-21T15:00:00.000' AS DateTime), CAST(N'2022-07-21T15:45:00.000' AS DateTime), N'2398a6ae-274c-4f6a-997b-2cae5ebd1f66')
